package Aula02_LOOP;

public class Exercicio09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int x = 1; x < 11;x++) {
			System.out.print(x+" ");}
		System.out.print("-- ");
		for (int y = 10;y > 0;y--) {
		
			System.out.print(y+" ");
			}
		
		}
	

}
